-- ----------------------------
-- Function structure for `genCatePidPath`
-- ----------------------------
DROP FUNCTION IF EXISTS `genCatePidPath`;
DELIMITER ;;
CREATE DEFINER=`wenku`@`%` FUNCTION `genCatePidPath`(`id_one` int) RETURNS char(100) CHARSET utf8
    READS SQL DATA
BEGIN
	DECLARE sTemp char (100);
	DECLARE sTempChd char (100);
	SET sTemp=null;
	SET sTempChd=cast(id_one AS CHAR);
	WHILE sTempChd is not null DO
		IF sTemp is not null THEN
			SET sTemp = concat(sTempChd,',',sTemp);
		END IF;
		IF sTemp is null THEN
			SET sTemp=sTempChd;
		END IF;
		SELECT
			GROUP_CONCAT(pid) INTO sTempChd
		FROM
			node_cate
		WHERE
			FIND_IN_SET(id,sTempChd);
	END WHILE;
	RETURN sTemp;
END
;;
DELIMITER ;

