INSERT INTO `cms_content` VALUES ('26', '27', '<p><span style=\"color:#333333;font-family:宋体, Arial, Tahoma, Helvetica, sans-serif;font-size:12px;line-height:23px;background-color:#FEFEF4;\">创建于1992年的武汉市育才中学，以科学发展观为指导，大力推进素质教育，坚持“质量为基、文化兴校、特色发展”的办学思路和“传承与发展陶行知教育思想、创造适合学生发展的新育才教育”特色发展主题，积极构建以校为本的生态文化、育人为本的生活德育、以生为本的生本生态课堂和以师为本的生机管理的新育才“四生”教育特色，促进了学生、教师和学校生动活泼的发展。学校是中国陶行知研究会实验学校和省陶行知研究会首个实验基地、省义务教育学校校长挂职和教师跟岗学习培训基地、市综合办学水平示范学校、市首批心理健康教育合格学校</span></p>', '121502');
