INSERT INTO `cms_content` VALUES ('125', '126', '<p style=\"font-family:Simsun;font-size:12px;line-height:28px;background-color:#FAF5F8\"><span style=\"font-size:19px;font-family:宋体\">创建目标：践行师德创先争优，办人民满意学校</span></p><p style=\"font-family:Simsun;font-size:12px;line-height:28px;background-color:#FAF5F8;text-indent:37px\"><span style=\"font-size:19px;font-family:宋体\">1</span><span style=\"font-size:19px;font-family:宋体\">、</span><span style=\"font-size:19px;font-family:宋体\">坚持党对教育工作的领导，</span><span style=\"font-size:19px;font-family:宋体\">坚决贯彻执行党的路线、方针、政策和决议，以科学发展观统领全局，</span><span style=\"font-size:19px;font-family:宋体\">充分发挥学校党组织的政治核心作用、战斗堡垒作用和监督保证作用，保证党的教育方针在学校得到全面贯彻落实。</span></p><p style=\"font-family:Simsun;font-size:12px;line-height:28px;background-color:#FAF5F8;text-indent:37px\"><span style=\"font-size:19px;font-family:宋体\">2</span><span style=\"font-size:19px;font-family:宋体\">、</span><span style=\"font-size:19px;font-family:宋体\">加强学校党的建设和教职工队伍政治思想建设，</span><span style=\"font-size:19px;font-family:宋体\">了解掌握教职工的基本情况和动态，做好教职工思想政治工作，调动教职工工作的积极性和创造性，为学校教育教学工作提供有力的思想基础和组织保障。</span></p><p style=\"font-family:Simsun;font-size:12px;line-height:28px;background-color:#FAF5F8;text-indent:37px\"><span style=\"font-size:19px;font-family:宋体\">3</span><span style=\"font-size:19px;font-family:宋体\">、加强干部思想作风与能力建设，造就一支“想干事能干事干成事不出事”的干部队伍，努力建设学习型、服务型、创新型、高效型领导集体。</span></p><p style=\"font-family:Simsun;font-size:12px;line-height:28px;background-color:#FAF5F8;text-indent:37px\"><span style=\"font-size:19px;font-family:宋体\">4</span><span style=\"font-size:19px;font-family:宋体\">、加强党员队伍建设，</span><span style=\"font-size:19px;font-family:宋体\">充分发挥共产党员在学校各项工作中的先锋模范作用，</span><span style=\"font-size:19px;font-family:宋体\">使党员成为服务师生、岗位奉献的鲜明旗帜。</span></p><p style=\"font-family:Simsun;font-size:12px;line-height:28px;background-color:#FAF5F8;text-indent:36px\"><span style=\"font-size:18px;font-family:宋体\">5</span><span style=\"font-size:18px;font-family:宋体\">、加强师德师风建设，严格执行《中小学教师职业道德规范》，开展师德教育，提高教师素质，树立良好形象。</span></p><p style=\"font-family:Simsun;font-size:12px;line-height:28px;background-color:#FAF5F8;text-indent:37px\"><span style=\"font-size:19px;font-family:宋体\">6</span><span style=\"font-size:19px;font-family:宋体\">、加强党风廉政建设，落实党风廉政建设责任制，营造公正透明、廉洁高效、求真务实的氛围。</span></p><p style=\"font-family:Simsun;font-size:12px;line-height:28px;background-color:#FAF5F8;text-indent:36px\"><span style=\"font-size:18px;font-family:宋体\">7</span><span style=\"font-size:18px;font-family:宋体\">、加强学校精神文明建设，热情为师生服务，</span><span style=\"font-size:19px;font-family:宋体\">做好事、办实事、解难事，切实解决好涉及师生切身利益的热点、难点问题。</span></p><p style=\"font-family:Simsun;font-size:12px;line-height:28px;background-color:#FAF5F8\"><span style=\"font-size:18px;font-family:宋体\">我们将主动认真接受上级党组织和人民群众的监督，认真兑现我们的承诺，用创先争优活动促进学校各项工作的开展。</span></p><p style=\"font-family:Simsun;font-size:12px;line-height:28px;background-color:#FAF5F8;text-indent:54px\"><span style=\"font-size:18px;font-family:宋体\">二〇一一年四月</span></p><p><br /></p>', '121502');
