INSERT INTO `cms_relate` VALUES ('187', '302', '学生2262人。学校师资力量雄厚', '', '', '8', '', '湖北省武昌水果湖二小位于美丽的东湖之滨，地处省委、省政府所在地，直属于省教育厅，学生主要来源于省直机关干部的子弟。始建于1962年，1978年独立成校，是一所规模较大的省级示范学校和首批武昌名校。　　学校现有42个教学班，在校学生2262人。学校师资力量雄厚，', 'a:5:{s:7:\"teacher\";N;s:7:\"student\";N;s:6:\"parent\";N;s:5:\"class\";N;s:5:\"grade\";N;}', '186', '121584', 'w36451978107373010061', '0', '0', '1', '187', '1420697996');
INSERT INTO `cms_relate` VALUES ('188', '302', '水二小家长开放日，实现家校零距离', '', '', '6', '', '为了让家长更深入地了解学校的教学以及孩子在学校的学习情况，11月25日到26日，水果湖第二小学开展了全校六个年级的家长开放日活动，让家长零距离的接触到孩子在学校的方方面面。超过1600多名家长进入学校旁听了学校的语文、数学、英语等学科的教学，还跟着学生一起去美', 'a:5:{s:7:\"teacher\";N;s:7:\"student\";N;s:6:\"parent\";N;s:5:\"class\";N;s:5:\"grade\";N;}', '187', '121584', 'w36451978107373010061', '0', '0', '1', '188', '1420699656');
